import React from 'react';
import { useState, Fragment } from 'react'
import { Listbox, Transition, Combobox } from '@headlessui/react'

const MahasiswaList = [
  "mahasiswa_1",
  "mahasiswa_2",
  "mahasiswa_3",
  "mahasiswa_4",
  "mahasiswa_5",
  "mahasiswa_6",
  "mahasiswa_7",
  "mahasiswa_8",
  "mahasiswa_9",
  "mahasiswa_10"
];

const AspekOptions = [
  { value: "aspek_1", label: "Aspek 1" },
  { value: "aspek_2", label: "Aspek 2" },
  { value: "aspek_3", label: "Aspek 3" }
];

const OptionValues = Array.from({ length: 10 }, (_, i) => i + 1);

function App() {

  const [ values, setValues ] = useState({});

  const handleChangeAspekPenilaian = (mahasiswa, aspek, selectedValue) => {
    setValues((prevValues) => ({
      ...prevValues,
      [ mahasiswa ]: {
        ...prevValues[ mahasiswa ],
        [ aspek ]: selectedValue
      }
    }));
  };

  const handleSave = () => {
    console.log(values);
  };


  return (
    <div className="container">
      <div className="flex justify-between">
        <div className="text-white lg:w-[100px] w-[200px]"></div>
        {AspekOptions.map((aspek) => (
          <div key={aspek.value} className="text-white lg:w-[200px] w-auto">
            {aspek.label}
          </div>
        ))}
      </div>
      {MahasiswaList.map((mahasiswa) => (
        <div key={mahasiswa} className="p-3 flex items-center justify-between bg-white rounded-lg">
          <div className="flex gap-2">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6">
              <path strokeLinecap="round" strokeLinejoin="round" d="M17.982 18.725A7.488 7.488 0 0012 15.75a7.488 7.488 0 00-5.982 2.975m11.963 0a9 9 0 10-11.963 0m11.963 0A8.966 8.966 0 0112 21a8.966 8.966 0 01-5.982-2.275M15 9.75a3 3 0 11-6 0 3 3 0 016 0z" />
            </svg>
            <div>{mahasiswa}</div>
          </div>
          {AspekOptions.map((aspek) => (
            <div key={aspek.value} className="lg:w-[200px] w-auto">
              <Listbox
                value={values[ mahasiswa ]?.[ aspek.value ]}
                onChange={(selectedValue) => handleChangeAspekPenilaian(mahasiswa, aspek.value, selectedValue)}
              >
                <div className="relative mt-1">
                  <Listbox.Button className="relative w-full cursor-default rounded-lg bg-white py-2 pl-3 pr-10 text-left shadow-md focus:outline-none focus-visible:border-indigo focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-opacity-75 focus-visible:ring-offset-2 focus-visible:ring-offset-yellow-600 sm:text-sm sm:leading-5"
                  >
                    <Listbox.Options
                      static
                      className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm sm:leading-5"
                    >
                      {OptionValues.map((option) => (
                        <Listbox.Option
                          key={option}
                          value={option}
                          className={({ active }) =>
                            `${active ? "text-white bg-indigo-600" : "text-gray-900"}
                            cursor-default select-none relative py-2 pl-3 pr-9`
                          }
                        >
                        {option}
                      </Listbox.Option>
                    ))}
                    </Listbox.Options>
                  </Listbox.Button>
                </div>
              </Listbox>
            </div>
          ))}
        </div>
      ))}
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 mt-4 rounded" onClick={handleSave}>
        Save
      </button>
    </div>
  );
}

export default App;
