import React from 'react';
import { useState, Fragment } from 'react'
import { Listbox, Transition, Combobox } from '@headlessui/react'

function App() {

  const MahasiswaList = [
    "mahasiswa_1",
    "mahasiswa_2",
    "mahasiswa_3",
    "mahasiswa_4",
    "mahasiswa_5",
    "mahasiswa_6",
    "mahasiswa_7",
    "mahasiswa_8",
    "mahasiswa_9",
    "mahasiswa_10",
  ];

  const initialValues = {
    aspek_penilaian_1: {},
    aspek_penilaian_2: {},
    aspek_penilaian_3: {},
  };

  const [ values, setValues ] = useState(initialValues);

  const handleChangeAspekPenilaian = (aspekPenilaian, mahasiswa, value) => {
    setValues((prevValues) => ({
      ...prevValues,
      [ aspekPenilaian ]: {
        ...prevValues[ aspekPenilaian ],
        [ mahasiswa ]: value,
      },
    }));
  };

  const handleSave = () => {
    console.log(values);
  };

  const [ state, setState ] = useState({
    aspek_penilaian_1: "",
    aspek_penilaian_2: "",
    aspek_penilaian_3: ""
  });

  const aspek_penilaian_1 = [
    { tipe: '1' },
    { tipe: '2' },
    { tipe: '3' },
    { tipe: '4' },
    { tipe: '5' },
    { tipe: '6' },
    { tipe: '7' },
    { tipe: '8' },
    { tipe: '9' },
    { tipe: '10' },
  ];

  const [ selectedAspekPenilaian1, setSelectedAspekPenilaian1 ] = useState("");

  function handleChangeAspekPenilaian1(selected) {
    setSelectedAspekPenilaian1(selected.tipe);
    setState(prevState => ({
      ...prevState,
      aspek_penilaian_1: selected.tipe,
    }));
  }


  const aspek_penilaian_2 = [
    { tipe: '1' },
    { tipe: '2' },
    { tipe: '3' },
    { tipe: '4' },
    { tipe: '5' },
    { tipe: '6' },
    { tipe: '7' },
    { tipe: '8' },
    { tipe: '9' },
    { tipe: '10' },
  ];

  const [ selectedAspekPenilaian2, setSelectedAspekPenilaian2 ] = useState("");

  function handleChangeAspekPenilaian2(selected) {
    setSelectedAspekPenilaian2(selected.tipe);
    setState(prevState => ({
      ...prevState,
      aspek_penilaian_2: selected.tipe,
    }));
  }


  const aspek_penilaian_3 = [
    { tipe: '1' },
    { tipe: '2' },
    { tipe: '3' },
    { tipe: '4' },
    { tipe: '5' },
    { tipe: '6' },
    { tipe: '7' },
    { tipe: '8' },
    { tipe: '9' },
    { tipe: '10' },
  ];

  const [ selectedAspekPenilaian3, setSelectedAspekPenilaian3 ] = useState("");

  function handleChangeAspekPenilaian3(selected) {
    setSelectedAspekPenilaian3(selected.tipe);
