import React from 'react';
import { useState, Fragment } from 'react'
import { Listbox, Transition, Combobox } from '@headlessui/react'

function App() {

  const [ state, setState ] = useState({
    articleType: "",
  });


  const typeArticles = [
    { tipe: 'BLOG' },
    { tipe: 'NEWSLETTER' },
  ];

  const [ selectedTypeArticle, setSelectedTypeArticle ] = useState("");

  function handleChangeTypeArticle(selected) {
    setSelectedTypeArticle(selected.tipe);
    setState(prevState => ({
      ...prevState,
      articleType: selected.tipe,
    }));
  }
  return (
    <div className="App">
      <div className="w-full">
        <Listbox value={selectedTypeArticle} onChange={handleChangeTypeArticle}>
          <div className="relative mt-1">
            <Listbox.Button className="relative w-full cursor-default rounded-lg bg-white py-2 pl-3 pr-10 text-left shadow-md focus:outline-none focus-visible:border-indigo-500 focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-opacity-75 focus-visible:ring-offset-2 focus-visible:ring-offset-yellow-600 sm:text-sm ">
              <span type="text" name="articleType" value={state.articleType} className="block truncate text-[#212529]">
                {selectedTypeArticle ? selectedTypeArticle : "Select type article"}
              </span>
              <span className="pointer-events-none absolute inset-y-0 right-0 flex items-center pr-2">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M8.25 15L12 18.75 15.75 15m-7.5-6L12 5.25 15.75 9" />
                </svg>
              </span>
            </Listbox.Button>
            <Transition
              as={Fragment}
              leave="transition ease-in duration-100"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <Listbox.Options className="absolute mt-1 max-h-60 w-full overflow-auto rounded-md bg-white py-1 text-base shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm">
                {typeArticles.map((typeArticles, x) => (
                  <Listbox.Option
                    key={x}
                    className={({ active }) =>
                      `relative cursor-default select-none py-2 pl-3 pr-4 ${active ? 'bg-[#FFC20A] text-black' : 'text-gray-900'
                      }`
                    }
                    value={typeArticles}
                  >
                    {({ selectedTypeArticle }) => (
                      <>
                        <span
                          className={`block truncate ${selectedTypeArticle ? 'font-medium' : 'font-normal'
                            }`}
                        >
                          {typeArticles.tipe}
                        </span>
                      </>
                    )}
                  </Listbox.Option>
                ))}
              </Listbox.Options>
            </Transition>
          </div>
        </Listbox>
      </div>
    </div>
  );
}

export default App;
